import getListStudents from './0-get_list_students.js';


function getListStudentIds(arg) {
    if (!Array.isArray(arr)) {
      return [];
    }

    else {
        const arr = arr.map(value => value.id);
        return arr;
    }
}

console.log(getListStudentIds('hello'));

